mapper.select:
 select b.* from 
 (
	select
		a.*,
	    rank() over(order by s_idx desc) as no    
    from 
    
    (
    	select seller.*,member.m_name,member.m_phone,member.m_email from seller left join member on seller.m_idx = member.m_idx
     	 		
			
				 s_local = #{s_local}
			
			
				 and s_field = #{s_field}
			
		
    ) a 
 ) b
 where no between #{ start } and #{ end }

mapper.update:
update seller set s_tcount=(
select ifnull(count(*),0) from thumb where thumb.s_idx = #{ s_idx }
)where seller.s_idx=#{s_idx}
